mapper.select:
	SELECT * FROM (
		SELECT rownum AS rn, 
		A.* FROM (
			SELECT b_num,b_writer,b_subject,b_date,b_readcount,b_show,b_secret, b_category
  				,(select count(*) from team_comment c where c.b_num=b.b_num) c_count 					
				FROM team_board b, team_user u 

				ORDER BY b_num DESC 
				) A
	) WHERE rn BETWEEN #{begin} AND #{end}
